DROP TABLE IF EXISTS b2c_class;
CREATE TABLE b2c_class (
   id int(10) unsigned NOT NULL auto_increment,
   name varchar(40),
   up_id int(10) unsigned NOT NULL,
   KEY id (id)
);




DROP TABLE IF EXISTS b2c_goods;
CREATE TABLE b2c_goods (
   id int(10) unsigned NOT NULL auto_increment,
   up_id int(10) unsigned NOT NULL,
   class_id int(10) unsigned NOT NULL,
   name varchar(60) NOT NULL,
   descript text NOT NULL,
   image varchar(40),
   price_m mediumint(9) NOT NULL,
   price mediumint(9) NOT NULL,
   state tinyint(4) NOT NULL,
   date datetime DEFAULT '0000-00-00 00:00:00' NOT NULL,
   KEY id (id)
);




DROP TABLE IF EXISTS b2c_requests;
CREATE TABLE b2c_requests (
   id int(10) unsigned NOT NULL auto_increment,
   user_id int(10) unsigned NOT NULL,
   name varchar(20) NOT NULL,
   sex tinyint(4) NOT NULL,
   email varchar(60) NOT NULL,
   province varchar(10) NOT NULL,
   city varchar(12) NOT NULL,
   tel varchar(40) NOT NULL,
   address varchar(100) NOT NULL,
   post varchar(6) NOT NULL,
   attrib text,
   fee decimal(16,2) DEFAULT '0.00' NOT NULL,
   pay tinyint(4) NOT NULL,
   send_out tinyint(4) NOT NULL,
   payment tinyint(4) NOT NULL,
   date_created datetime DEFAULT '0000-00-00 00:00:00' NOT NULL,
   KEY id (id)
);




DROP TABLE IF EXISTS b2c_shopping;
CREATE TABLE b2c_shopping (
   id int(10) unsigned NOT NULL auto_increment,
   requests_id int(10) unsigned NOT NULL,
   user_id int(10) unsigned NOT NULL,
   goods_id int(10) unsigned NOT NULL,
   goods_num mediumint(9) NOT NULL,
   date_created datetime DEFAULT '0000-00-00 00:00:00' NOT NULL,
   KEY id (id)
);




DROP TABLE IF EXISTS b2c_user;
CREATE TABLE b2c_user (
   id int(10) unsigned NOT NULL auto_increment,
   u_name varchar(16) NOT NULL,
   u_pass varchar(16) NOT NULL,
   name varchar(20) NOT NULL,
   sex tinyint(4) NOT NULL,
   email varchar(60) NOT NULL,
   province varchar(10) NOT NULL,
   city varchar(12) NOT NULL,
   tel varchar(40) NOT NULL,
   address varchar(100) NOT NULL,
   post varchar(6) NOT NULL,
   attrib text,
   paper_name varchar(6) NOT NULL,
   paper_num varchar(25) NOT NULL,
   zzbh varchar(100),
   khhh varchar(100),
   khzh varchar(100),
   reg_date date DEFAULT '0000-00-00' NOT NULL,
   last_date datetime DEFAULT '0000-00-00 00:00:00' NOT NULL,
   times int(10) unsigned NOT NULL,
   action enum('y','n') DEFAULT 'y' NOT NULL,
   KEY id (id)
);




